import React, { useState } from "react";
import "./item.css";
import { Text, Image, Box, Button, Input } from "@chakra-ui/react";
import { useDataContext } from "../../context/ContextProvider";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../../store/slices/cartSlice";

const Item = ({ PId, PName, Price, UpdateToggle }) => {
  const { handleRemove, handleUpdate, handleToggle } = useDataContext();
  const [newName, setNewName] = useState("");
  const [newPrice, setNewPrice] = useState("");

  const dispatch = useDispatch();

  let data;

  const handleName = (e) => {
    setNewName(e.target.value);
  };

  const handlePrice = (e) => {
    setNewPrice(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    data = { PId: PId, PName: newName, Price: newPrice };
    console.log("submit", data);
    handleUpdate(data);
    handleToggle(PId);
  };
  const state = useSelector((state) => state.ProductsReducer);
  const { products } = state;
  const { isFormOpen } = products;
  return (
    <div className="item">
      <Box>
        <Image
          boxSize="250px"
          objectFit="cover"
          src="https://bit.ly/dan-abramov"
          alt="Dan Abramov"
        />
      </Box>
      <div className="itemName">{PName}</div>
      <div className="itemPrice">
        <Text fontSize="20px">Rs.{Price}</Text>
      </div>
      <Button colorScheme="red" onClick={() => handleRemove(PId)}>
        Remove
      </Button>
