      >
        Update
      </Button>

      <Button
        mt="10px"
        type="submit"
        colorScheme="green"
        onClick={() => dispatch(addToCart(PId))}
      >
        Add to Cart
      </Button>
      <Box display={isFormOpen ? "block" : "none"}>
        formtoggle.id===id && formtoggle.value?
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="newPName"
            placeholder="Name"
            value={newName}
            onChange={handleName}
          />
          <input
            type="number"
            name="newPrice"
            placeholder="Price"
            value={newPrice}
            onChange={handlePrice}
          />
          <Button type="submit" colorScheme="green">
            Submit
          </Button>
        </form>
      </Box>
    </div>
  );
};

export default Item;
