import Item from "../item/Item";
import { Button, Flex } from "@chakra-ui/react";
import AddItem from "../addItem/AddItem";
import "./main.css";
import { useDataContext } from "../../context/ContextProvider";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const Main = () => {
  const products = useSelector((state) => state.ProductsReducer.products);
  const state = useSelector((state) => state.SearchReducer);
  const { searchValue, searchedItem } = state;
  const [newItemToggle, setNewItemToggle] = useState();

  const handleAddNewItem = () => {
    setNewItemToggle(newItemToggle ? false : true);
  };

  useEffect(() => {
    if (products.length) {
      localStorage.setItem("products", JSON.stringify(products));
    }
  }, [products]);

  return (
    <div className="main">
      <Button onClick={handleAddNewItem}>AddNewItem</Button>
      <AddItem display={newItemToggle ? "block" : "none"} />
      <Flex
        width="90%"
        display="flex"
        flexWrap="wrap"
        justifyContent="space-around"
        gap="30px"
        margin="40px"
      >
        {searchValue === ""
          ? products.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })
          : searchedItem.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })}
      </Flex>
    </div>
  );
};

export default Main;
