import Item from "../item/Item";
import { Box, Button, Flex } from "@chakra-ui/react";
import AddItem from "../addItem/AddItem";
import "./main.css";
import { useDataContext } from "../../context/ContextProvider";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const Main = () => {
  const products = useSelector((state) => state.ProductsReducer.products);
  const state = useSelector((state) => state.SearchReducer);
  const { searchValue, searchedItem } = state;
  const [newItemToggle, setNewItemToggle] = useState(false);

  const handleAddNewItem = () => {
    console.log("beforetoggle", newItemToggle);
    setNewItemToggle(!newItemToggle);
    console.log("aftertoggle", newItemToggle);
  };

  useEffect(() => {
    if (products.length) {
      localStorage.setItem("products", JSON.stringify(products));
    }
  }, [products]);

  return (
    <div className="main" style={{ position: "relative" }}>
      <Button
        w="auto"
        style={{
          color: "blue",
          position: "absolute",
          top: "-50px",
          right: "0px",
        }}
        onClick={handleAddNewItem}
      >
        AddNewItem
      </Button>
      <Box display={newItemToggle ? "block" : "none"}>
        <AddItem />
      </Box>
      <Flex
        width="90%"
        display="flex"
        flexWrap="wrap"
        justifyContent="space-around"
        gap="30px"
        margin="40px"
      >
        {searchValue === ""
          ? products.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })
          : searchedItem.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })}
      </Flex>
    </div>
  );
};

export default Main;
