import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  AlertDialog,
  AlertDialogBody,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogOverlay,
  Box,
  Button,
  Center,
  Flex,
  useDisclosure,
} from "@chakra-ui/react";

import { useToast } from "@chakra-ui/react";

import Item from "../item/Item";
import AddItem from "../addItem/AddItem";

import { toggleAddItem } from "../../store/slices/productsSlice";

import "./main.css";
import { isVisible } from "@testing-library/user-event/dist/utils";

const Main = () => {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.ProductsReducer.products);
  const searchReducerState = useSelector((state) => state.SearchReducer);
  const { searchValue, searchedItem } = searchReducerState;

  const alertReducerState = useSelector((state) => state.AlertSliceReducer);

  const { type, message, key } = alertReducerState;

  const toast = useToast({
    position: "bottom-right",
    title: type,
    containerstyle: {
      width: "800px",
    },
  });

  const toggle = useSelector((state) => state.ProductsReducer.toggle);

  const handleAddNewItem = () => {
    dispatch(toggleAddItem());
  };

  useEffect(() => {
    if (products.length) {
      localStorage.setItem("products", JSON.stringify(products));
    }
  }, [products]);

  const {
    isOpen: isVisible,
    onClose,
    onOpen,
  } = useDisclosure({ defaultIsOpen: true });

  useEffect(() => {
    console.log("alert Reducer", alertReducerState);
    if (type === "success") {
      toast({
        title: type,
        description: message,
        status: type,
        duration: 3000,
        isClosable: false,
      });
    } else if (type === "warning") {
      isVisible = true;
    }
  }, [key]);

  return (
    <div className="main">
      {isVisible ? (
        <AlertDialog
          isOpen={isOpen}
          leastDestructiveRef={cancelRef}
          onClose={onClose}
        >
          <AlertDialogOverlay>
            <AlertDialogContent>
              <AlertDialogHeader fontSize="lg" fontWeight="bold">
                Delete Customer
              </AlertDialogHeader>

              <AlertDialogBody>
                Are you sure? You can't undo this action afterwards.
              </AlertDialogBody>

              <AlertDialogFooter>
                <Button ref={cancelRef} onClick={onClose}>
                  Cancel
                </Button>
                <Button colorScheme="red" onClick={onClose} ml={3}>
                  Delete
                </Button>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialogOverlay>
        </AlertDialog>
      ) : null}
      <Button
        colorScheme="blue"
        w="auto"
        style={{
          position: "absolute",
          top: "115px",
          right: "5px",
        }}
        onClick={handleAddNewItem}
      >
        AddNewItem
      </Button>
      <Box display={toggle ? "block" : "none"} alignItems="center" mt="10px">
        <Center>
          <AddItem />
        </Center>
      </Box>
      <Flex
        width="90%"
        display="flex"
        flexWrap="wrap"
        justifyContent="space-around"
        gap="30px"
        margin="60px 40px"
      >
        {searchValue === ""
          ? products.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })
          : searchedItem.map((item) => {
              return (
                <Item
                  key={item.PId}
                  PId={item.PId}
                  PName={item.PName}
                  Price={item.Price}
                  formToggle={item.formToggle}
                />
              );
            })}
      </Flex>
    </div>
  );
};

export default Main;
