import React, { useState } from "react";
import { useDispatch } from "react-redux";

import { Text, Image, Box, Button, Input, Flex } from "@chakra-ui/react";
import { Tooltip } from "@chakra-ui/react";
import DriveFileRenameOutlineRoundedIcon from "@mui/icons-material/DriveFileRenameOutlineRounded";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";

import { addToCart } from "../../store/slices/cartSlice";
import {
  updateFormToggle,
  updateProducts,
  removeItem,
} from "../../store/slices/productsSlice";
import "./item.css";

const Item = ({ PId, PName, Price, formToggle }) => {
  const [newName, setNewName] = useState();
  const [newPrice, setNewPrice] = useState();

  const dispatch = useDispatch();

  let data;

  const handleName = (e) => {
    setNewName(e.target.value);
  };

  const handlePrice = (e) => {
    setNewPrice(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    data = { PId, PName: newName, Price: newPrice };
    console.log("submit", data);
    dispatch(updateProducts(data));
    dispatch(updateFormToggle(PId));
  };
  const handleAddToCart = () => {
    const tempData = {
      PId,
      PName,
      Price,
    };
    dispatch(addToCart(tempData));
  };
  return (
    <div className="item">
      <Box>
        <Image
          boxSize="250px"
          objectFit="cover"
          src="https://bit.ly/dan-abramov"
          alt="Dan Abramov"
        />
      </Box>
      <Box padding="20px">
        <div className="itemName">{PName}</div>
        <div className="itemPrice">
          <Text fontSize="20px">Rs.{Price}</Text>
        </div>
        <Flex w="100%" justifyContent="space-around">
          <Tooltip label="addtocart">
            <AddShoppingCartIcon
              style={{ color: "blue" }}
              onClick={handleAddToCart}
            />
          </Tooltip>
          <DriveFileRenameOutlineRoundedIcon
            style={{ color: "green" }}
            onClick={() => dispatch(updateFormToggle(PId))}
          />
          <DeleteOutlineIcon
            style={{ color: "red" }}
            onClick={() => dispatch(removeItem(PId))}
          />
        </Flex>
      </Box>

      <Box
        display={formToggle ? "block" : "none"}
        style={{ position: "absolute" }}
        w="250px"
      >
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="newPName"
            placeholder="Name"
            value={newName}
            onChange={handleName}
          />
          <input
            type="number"
            name="newPrice"
            placeholder="Price"
            value={newPrice}
            onChange={handlePrice}
          />
          <Button type="submit" colorScheme="green">
            Submit
          </Button>
        </form>
      </Box>
    </div>
  );
};

export default Item;
