import React, { useEffect, useState } from "react";
import { useDataContext } from "../../context/ContextProvider";

import { Box, Flex } from "@chakra-ui/react";
import CartItem from "../../components/cartItem/CartItem";
import { useDispatch, useSelector } from "react-redux";
import { addItemToCart, loadCart } from "../../store/slices/cartSlice";

const CartPage = () => {
  const { cart } = useDataContext();
  const [total, setTotal] = useState();
  const dispatch = useDispatch();
  useEffect(() => {
    let t = 0;
    cart.forEach((item) => {
      t = t + item.Price * item.quantity;
    });
    setTotal(t);
  }, [cart]);
  useEffect(() => {
    dispatch(loadCart);
    dispatch(addItemToCart({ id: "1", name: "Shoes" }));
  }, []);
  const state = useSelector((state) => state.CartReducer);
  const { cartDetails, newItem } = state;
  console.log("This is cart details", cartDetails, newItem);
  // console.log("this is state from cart", state);

  return (
    <Flex>
      <Box w="80%">
        {cart.map((item) => {
          return (
            <CartItem
              key={item.PId}
              PId={item.PId}
              PName={item.PName}
              Price={item.Price}
              quantity={item.quantity}
            />
          );
        })}
      </Box>
      <Box>
        <Box>Subtotal</Box>
        <Box>{total}</Box>
      </Box>
    </Flex>
  );
};

export default CartPage;
