import { configureStore } from "@reduxjs/toolkit";
import CartReducer from "./slices/cartSlice";
import loginSliceReducer from "./slices/loginSlice";

export const store = configureStore({
  reducer: {
    login: loginSliceReducer,
    cart: CartReducer,
  },
});

